import React, { useState } from "react";
import { useNavigate } from "react-router-dom";

const Login = () => {
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");
    const navigate = useNavigate();

    app.post("/api/login", (req, res) => {
        //ðŸ‘‡ðŸ» Accepts the user's credentials
        const { email, password } = req.body;
        //ðŸ‘‡ðŸ» Checks for user(s) with the same email and password
        let result = users.filter(
            (user) => user.email === email && user.password === password
        );
    
        //ðŸ‘‡ðŸ» If no user exists, it returns an error message
        if (result.length !== 1) {
            return res.json({
                error_message: "Incorrect credentials",
            });
        }
        //ðŸ‘‡ðŸ» Returns the username of the user after a successful login
        res.json({
            message: "Login successfully",
            data: {
                username: result[0].username,
            },
        });
    });

    const handleSubmit = (e) => {
        e.preventDefault();
        console.log({ email, password });
        setPassword("");
        setEmail("");
    };

    const gotoSignUpPage = () => navigate("/register");

    return (
        <div className='login__container'>
            <h2>Login </h2>
            <form className='login__form' onSubmit={handleSubmit}>
                <label htmlFor='email'>Email</label>
                <input
                    type='text'
                    id='email'
                    name='email'
                    value={email}
                    required
                    onChange={(e) => setEmail(e.target.value)}
                />
                <label htmlFor='password'>Password</label>
                <input
                    type='password'
                    name='password'
                    id='password'
                    minLength={8}
                    required
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                />
                <button className='loginBtn'>SIGN IN</button>
                <p>
                    Don't have an account?{" "}
                    <span className='link' onClick={gotoSignUpPage}>
                        Sign up
                    </span>
                </p>
            </form>
        </div>
    );
};

export default Login;
